App.controller("EventsController",["$scope","events",function($scope,events){var loadContents=function(){events.getEvents().then(function(result){var response=result,eventsList=response.posts,index=0;_.each(eventsList,function(event){var date=new Date(event.date.replace(" ","T")),day=date.getDate()>=10?date.getDate():"0"+date.getDate(),month=date.getMonth()>=10?date.getMonth():"0"+date.getMonth(),year=date.getFullYear();if(event.date=day+"/"+month+"/"+year,index%10===0){var page={eventsList:[]};$scope.eventsPages.push(page)}$scope.eventsPages[$scope.eventsPages.length-1].eventsList.push(event),index++})})};loadContents(),$scope.eventsPages=[],$scope.eventsSelectedPage=0,console.log("Loaded events controller")}]);